edicates\";a:1:{i:0;s:16:\"sioc:has_creator\";}s:4:\"type\";s:3:\"rel\";}s:4:\"name\";a:1:{s:10:\"predicates\";a:1:{i:0;s:9:\"foaf:name\";}}s:13:\"comment_count\";a:2:{s:10:\"predicates\";a:1:{i:0;s:16:\"sioc:num_replies\";}s:8:\"datatype\";s:11:\"xsd:integer\";}s:13:\"last_activity\";a:3:{s:10:\"predicates\";a:1:{i:0;s:23:\"sioc:last_activity_date\";}s:8:\"datatype\";s:12:\"xsd:dateTime\";s:8:\"callback\";s:12:\"date_iso8601\";}}s:3:\"cid\";s:1:\"0\";s:22:\"last_comment_timestamp\";s:10:\"1454929181\";s:17:\"last_comment_name\";N;s:16:\"last_comment_uid\";s:1:\"1\";s:13:\"comment_count\";s:1:\"0\";s:4:\"name\";s:5:\"admin\";s:7:\"picture\";s:1:\"0\";s:4:\"data\";s:241:\"a:7:{s:5:\"block\";a:1:{s:6:\"system\";a:1:{s:4:\"main\";i:0;}}s:16:\"ckeditor_default\";s:1:\"t\";s:20:\"ckeditor_show_toggle\";s:1:\"t\";s:14:\"ckeditor_width\";s:4:\"100%\";s:13:\"ckeditor_lang\";s:2:\"en\";s:18:\"ckeditor_auto_lang\";s:1:\"t\";s:7:\"overlay\";i:1;}\";}',0,1458184477,1),('9275','O:8:\"stdClass\":39:{s:3:\"vid\";s:4:\"9279\";s:3:\"uid\";s:1:\"1\";s:5:\"title\";s:63:\"Member Benefit: The Law Society’s Annual Christmas Party 2015\";s:3:\"log\";s:29:\"Created by FeedsNodeProcessor\";s:6:\"status\";s:1:\"1\";s:7:\"comment\";s:1:\"1\";s:7:\"promote\";s:1:\"0\";s:6:\"sticky\";s:1:\"0\";s:3:\"nid\";s:4:\"9275\";s:4:\"type\";s:7:\"article\";s:8:\"language\";s:2:\"en\";s:7:\"created\";s:10:\"1454284800\";s:7:\"changed\";s:10:\"1455078380\";s:4:\"tnid\";s:4:\"9275\";s:9:\"translate\";s:1:\"0\";s:18:\"revision_timestamp\";s:10:\"1455078380\";s:12:\"revision_uid\";s:3:\"217\";s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:1233:\"\r\n\r\n<p>The Annual Christmas Party, organised by the Member Benefit Committee, was successfully held on 17 December 2015. Over 250 members and guests celebrated the festive season together at this<em> “White Christmas”</em> themed event.</p>\r\n\r\n<p>The Party began with young children from St. Stephen’s Church Kindergarten singing Christmas carols which was followed by non-stop performances and surprises, including a magic show and group games. Four fun stalls were also set up this year; and a number of lucky members received Christmas presents from our handsome Santa Claus. Members and guests had a fun time posing for pictures and portraits and serving themselves popcorns and candy floss. The Party concluded with the wonderful live performance from our band TLF.</p>\r\n\r\n<p><img alt=\"\" height=\"358\" src=\"/uploads/images/Issues/Feb2016/LSN_24.jpeg\" width=\"538\" /><img alt=\"\" height=\"358\" src=\"/uploads/images/Issues/Feb2016/LSN_25.jpeg\" width=\"538\" /><img alt=\"\" height=\"321\" src=\"/uploads/images/Issues/Feb2016/LSN_26.jpeg\" width=\"538\" /><img alt=\"\" height=\"349\" src=\"/uploads/images/Issues/Feb2016/LSN_27.jpeg\" width=\"538\" /><img alt=\"\" height=\"414\" src=\"/uploads/images/Issues/Feb2016/LSN_28.jpeg\" width=\"538\" /></p>\r\n\";s:7:\"summary\";s:25:\"<p>Law Society News</p>\r\n\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:1222:\"<p>The Annual Christmas Party, organised by the Member Benefit Committee, was successfully held on 17 December 2015. Over 250 members and guests celebrated the festive season together at this<em> “White Christmas”</em> themed event.</p>\n<p>The Party began with young children from St. Stephen’s Church Kindergarten singing Christmas carols which was followed by non-stop performances and surprises, including a magic show and group games. Four fun stalls were also set up this year; and a number of lucky members received Christmas presents from our handsome Santa Claus. Members and guests had a fun time posing for pictures and portraits and serving themselves popcorns and candy floss. The Party concluded with the wonderful live performance from our band TLF.</p>\n<p><img alt=\"\" height=\"358\" src=\"/uploads/images/Issues/Feb2016/LSN_24.jpeg\" width=\"538\" /><img alt=\"\" height=\"358\" src=\"/uploads/images/Issues/Feb2016/LSN_25.jpeg\" width=\"5